import React from 'react';
import './sidebar.css';
import TheatersOutlinedIcon from '@mui/icons-material/TheatersOutlined';
import SlideshowIcon from '@mui/icons-material/Slideshow';
import LocalActivityOutlinedIcon from '@mui/icons-material/LocalActivityOutlined';

function Sidebar() {
  const genres = [
    'Action',
    'Adventure',
    'Animation',
    'Comedy',
    'Crime',
    'Documentary',
    'Drama',
    'Family',
    'Fantasy',
    'Horror',
    'Musical',
    'Mystery',
    'Romance',
    'Sci-Fi',
    'Thriller',
    'Western',
  ];

  return (
    <div className='sidebar-container'>
      <div className='categories'>
        <ul>
          <li><a href="/" className='category-icon-cont'><TheatersOutlinedIcon sx={{ fontSize: 28 }} className='icon'/> Films</a></li>
          <li><a href="/" className='category-icon-cont'><SlideshowIcon sx={{ fontSize: 28 }} className='icon'/>TV-Shows</a></li>
          <li><a href="/" className='category-icon-cont'><LocalActivityOutlinedIcon sx={{ fontSize: 28 }} className='icon'/>Top 250 Films</a></li>
        </ul>
      </div>
      
      <div className='genres'>
        <p>GENRES</p>
        <ul>
          {genres.map((genre, index) => (
            <li key={index} className={index % 2 === 0 ? 'whiteRow' : 'grayRow'}>
              <a href="/">{genre}</a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Sidebar;
