import React, { useState } from 'react';
import StarBorderOutlinedIcon from '@mui/icons-material/StarBorderOutlined';
import StarOutlinedIcon from '@mui/icons-material/StarOutlined';
import StarHalfOutlinedIcon from '@mui/icons-material/StarHalfOutlined';
import StarIcon from '@mui/icons-material/Star';
import './ratingStars.scss';

function RatingStars() {
  const [rating, setRating] = useState(0); // Состояние для хранения выбранного рейтинга

  const handleStarClick = (value) => {
    setRating(value); // Обновление состояния рейтинга при клике на звезду
  };
  return (
    <div className="movieRating" onClick={() => handleStarClick(0)}>
    {rating >= 0.5 ? <StarIcon sx={{ color: '#FFD700' }} /> : <StarBorderOutlinedIcon />}
    {rating >= 1.5 ? <StarIcon sx={{ color: '#FFD700' }} /> : rating >= 1 ? <StarHalfOutlinedIcon /> : <StarBorderOutlinedIcon />}
    {rating >= 2.5 ? <StarIcon sx={{ color: '#FFD700' }} /> : rating >= 2 ? <StarHalfOutlinedIcon /> : <StarBorderOutlinedIcon />}
    {rating >= 3.5 ? <StarIcon sx={{ color: '#FFD700' }} /> : rating >= 3 ? <StarHalfOutlinedIcon /> : <StarBorderOutlinedIcon />}
    {rating >= 4.5 ? <StarIcon sx={{ color: '#FFD700' }} /> : rating >= 4 ? <StarHalfOutlinedIcon /> : <StarBorderOutlinedIcon />}
  </div>
  )
}

export default RatingStars
