import React, { useState } from 'react';

function RatingStart() {
  const [rating, setRating] = useState(0); // initial rating is 0
  const [hoverRating, setHoverRating] = useState(0); // hover rating for hover effect

  const handleRatingChange = (newRating) => {
    setRating(newRating);
  };

  const handleHover = (hoverRating) => {
    setHoverRating(hoverRating);
  };

  const stars = [];
  for (let i = 0; i < 5; i++) {
    stars.push(
      <span
        key={i}
        className={`star ${i < rating ? 'filled' : ''} ${i < hoverRating ? 'hover' : ''}`}
        onClick={() => handleRatingChange(i + 1)}
        onMouseEnter={() => handleHover(i + 1)}
        onMouseLeave={() => handleHover(0)}
      >
        {i < rating ? '' : ''}
      </span>
    );
  }

  return (
    <div className="rating-container">
      {stars}
      <span className="rating-value">{rating} / 10</span>
  </div>
  )
}

export default RatingStart
