import React from 'react'
import './moviePageContent.css';

function MoviePageContent() {
  const description = [
    'Title',
    'Year',
    'Rating',
    'Countries',
    'Genres',
    'Director',
    'Cast'
  ];


  return (
    <div className='moviePageContent'>
      <div className="descInfo">
        <ul>
            {description.map((description, index) => (
              <li key={index} className={index % 2 === 0 ? 'darkRow' : 'lightRow'}>
                <p>{description} <span>Title and something</span></p> {/*здесь будет потом переменная*/}
              </li>
            ))}
          </ul>
      </div>
      <div className="description">
        <p>On a faraway planet where blue giants rule, oppressed humanoids rebel against their machine-like leaders.</p>
        
      </div>
    </div>
  )
}

export default MoviePageContent
