import React, { useState } from 'react'
import './newReview.css';
import { Avatar } from '@mui/material';
import { deepPurple } from '@mui/material/colors';
import StarOutlineIcon from '@mui/icons-material/StarOutline';
import StarHalfIcon from '@mui/icons-material/StarHalf';
import StarRateIcon from '@mui/icons-material/StarRate';

function NewReview() {
  const [rating, setRating] = useState(0); // initial rating is 0
  const [hoverRating, setHoverRating] = useState(0); // hover rating for hover effect

  const handleStarClick = (value) => {
    setRating(value);
  };

  const handleStarHover = (value) => {
    setHoverRating(value);
  };

  const handleStarLeave = () => {
    setHoverRating(0);
  };

  return (
    <div className='new-review'>
      <div className="userInfoReview">
        <Avatar sx={{ bgcolor: deepPurple[500] }} className='review-Avatar'>U</Avatar>
        <p className="userName">USER</p>
        <div className="rating_stars">
          {Array(5).fill(0).map((_, index) => (
            <div
              key={index}
              className="movieRating"
              onClick={() => handleStarClick(index + 0.5)}
              onMouseEnter={() => handleStarHover(index + 0.5)}
              onMouseLeave={handleStarLeave}
            >
              {rating >= index + 0.5 ? (
                <StarRateIcon sx={{ color: '#FFD700' }} />
              ) : hoverRating >= index + 0.5 ? (
                <StarHalfIcon />
              ) : (
                <StarOutlineIcon />
              )}
            </div>
          ))}
        </div>
      </div>
      <textarea
        id="review-input"
        placeholder="Write your review here..."
        className="review-input"
      />
      <div className="button_submit">
        <button id="submit-review">Submit Review</button>
      </div>
    </div>
  );
}

export default NewReview;
