import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import React from "react";
import Slider from "react-slick";
import './fadeSlider.css'

import barbie_img from '../../images/home/barbie.jpg'
import evil_dead_img from '../../images/home/evil_dead_rise.jpg'
import ind_jns_img from '../../images/home/indiana_jones_5.jpg'
import terr_img from '../../images/home/terrifier.jpg'

function FadeSlider() {
  const settings = {
    dots: true,
    fade: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    waitForAnimate: false,
    speed: 500,
    autoplay: true, 
    autoplaySpeed: 5000
  };
  return (
    <div className="slider-container">
      <Slider {...settings}>
        <div>
          <img src={barbie_img} />
        </div>
        <div>
          <img src={evil_dead_img} />
        </div>
        <div>
          <img src={ind_jns_img} />
        </div>
        <div>
          <img src={terr_img} />
        </div>
      </Slider>
    </div>
  );
}

export default FadeSlider;
