import React, { useState } from 'react'
import './filmTitle.css'
import poster from '../../images/fantastic_planet.jpg';
import { green, grey, orange, red } from '@mui/material/colors';
import PlayCircleIcon from '@mui/icons-material/PlayCircle';
import VisibilityOutlinedIcon from '@mui/icons-material/VisibilityOutlined';
import VisibilityIcon from '@mui/icons-material/Visibility';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import WatchLaterIcon from '@mui/icons-material/WatchLater';
import StarBorderOutlinedIcon from '@mui/icons-material/StarBorderOutlined';

function FilmTitle() {
  return (
    <div>
        <div className='movie-container'>
          <div className="titleInfo">
            <p className="title">Title will be here</p>
            <div className="icons">
              <div className="movieRating">
                <StarBorderOutlinedIcon sx={{ color: orange[500], fontSize: 30}}/>
                <p className="rating">8.4</p>
              </div>
                <span onClick={handleIsFavoriteClick} className='fav-icon'>
                  {isFavorite ? <FavoriteIcon sx={{ color: red[500], fontSize: 28 }} /> : <FavoriteBorderIcon sx={{ color: grey[500], fontSize: 28 }} />}
                </span>
              <span onClick={handleIsWatchedClick} className='watched-icon'>
                {isWatched ? <VisibilityIcon sx={{ color: green[500], fontSize: 28 }} /> : <VisibilityOutlinedIcon sx={{ color: grey[500], fontSize: 28 }} />}
              </span>
              <span onClick={handleIsWatchLaterClick} className='watch-later-icon'>
                  {isWatchLater ? <WatchLaterIcon sx={{ color: grey[500], fontSize: 28 }} /> : <AccessTimeIcon sx={{ color: grey[500], fontSize: 28 }} />}
              </span>
            </div>
          </div>
          <hr />
          {/* <img src={poster} alt="" /> */}
        </div>
    </div>
  )
}

export default FilmTitle
