    'Title',
    'Year',
    'Rating',
    'Countries',
    'Genres',
    'Director',
    'Cast'
  ];


  return (
    <div className='moviePageContent'>
      <div className="descInfo">
        <ul>
            {description.map((description, index) => (
              <li key={index} className={index % 2 === 0 ? 'darkRow' : 'lightRow'}>
                <p>{description} <span>Title and something</span></p> {/*здесь будет потом переменная*/}
              </li>
